import React, { Component } from 'react';
//import axios from 'axios';
import{Button} from "reactstrap";
//import axios from 'axios';
class TableRowUser extends Component {
    
    onClickEdit=()=>{
        this.props.onView();
        this.props.onChangeTT();
    }
    hienThiRole =()=>{
        if(this.props.obj.role ===1) {return "admin";}
        else {return "Staff";}
    }
    hienThiSex=()=>{
        if(this.props.obj.sex === true) return "mail";
        else {return }
    }
    render() {
        return (
            <tr>
                
                <td>
                    {this.props.obj.iduser}
                </td>
                <td>
                    {this.props.obj.firstname}
                </td>
                <td>
                    {this.props.obj.lastname}
                </td>
                <td>
                    {this.props.obj.sex}
                </td>
                <td>
                    {this.props.obj.dob}
                </td>
                <td>
                    {this.props.obj.phonenumber}
                </td>
                <td>
                    {this.props.obj.address}
                </td>
                <td>
                    {this.props.obj.username}
                </td>
                
                <td>
                    {this.hienThiRole()}    
                </td>
                <td>
                    <Button onClick={()=>this.onClickEdit()} size="sm" color="info">Edit</Button>
                    <Button size="sm" color="danger">Delete</Button>
                </td>
                
            </tr>
        );
    }
}

export default TableRowUser;
