import React from "react";
import axios from 'axios';
// react plugin for creating notifications over the dashboard
import NotificationAlert from "react-notification-alert";
import 'react-toastify/dist/ReactToastify.css';
import TableRow from "components/TableRow/TableRowDoc.js";
import {Redirect } from "react-router-dom";
// reactstrap components
import { 
  Card,
  CardBody,
  Row,
  Label,
  Input,
  FormGroup,
  Table,
  Col
} from "reactstrap";
import CardHeader from "reactstrap/lib/CardHeader";
import Button from "reactstrap/lib/Button";

class Notifications extends React.Component {

  constructor(props) {
    super(props);
    this.onChangeValue = this.onChangeValue.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.state = {
      data:[]  
    }
  }
  componentDidMount() {
    axios.get('/api/doc/all')
        .then(response => {
            console.log(response.data);
            this.setState({data: response.data});
        })
        .catch(function (error) {
            console.log(error);
        })
  }
  onChangeValue(event){
    this.setState({
      key:event.target.value
    });
  }
  onSubmit(event){
    event.preventDefault();
    const b ={ 
      key:this.state.key
    };
    axios.post('/api/doc/search',b)
    .then((res)=>{
      console.log(res.data)
      this.setState({
        
      })
    })
  }
  tabRow() {
    return this.state.data.map(function (object, i) {
        return <TableRow obj={object} key={i}/>;
    });
  } 
  render() {
    if (!localStorage.getItem('authorization')) return <Redirect to="/login" />
    return (
        <div className="content">
          <div className="react-notification-alert-container">
            <NotificationAlert ref="notificationAlert" />
          </div>
          <Row>
            <Col md="8">
            <Card>
              <CardBody>
              <FormGroup row>
                <Label sm={2} tag="h6">Tìm kiếm</Label>
                <Col sm={5}>
                  
                    <Input  onChange={this.onChangeValue} type="text" name="key" id="" placeholder="tài liệu muốn tìm kiếm"/>
                    
                 
                   
                </Col>
                <Col sm={2}>
                  <Button onClick={this.onSubmit}>tìm</Button>
                </Col>
                
              </FormGroup>
              </CardBody>
            </Card> 
            </Col>
          </Row>
          <Row>          
            <Col md="10">
              <Card>               
                <CardHeader>
                  <FormGroup row>
                    <Label md="6"  sm={6} tag="h6">Danh sách tài liệu</Label>
                      <Col md="6" sm={6}>
                        <Input md="2" value={this.state.subject} onChange={this.onchangValue} type="select" name="subject" id="">
                              {this.state.data.map((list,i) => (
                                  <option key={i} value={list.filename}>{list.filename}</option>
                              ))}
                        </Input>
                      </Col>
                  </FormGroup>
                </CardHeader>   
                  <CardBody>
                    <Table striped>
                        <thead>
                          <tr>
                            <th>ID</th>
                            <th>Filename</th>
                            <th>Authorname</th>
                            <th>Note</th>
                            <th>Action</th>
                          </tr>
                        </thead>
                        <tbody>
                          {this.tabRow()}
                        </tbody>
                      </Table>
                </CardBody>
              </Card>
            </Col>           
          </Row>
        </div>
    );
  }
}

export default Notifications;
