import React, { Component } from 'react';
//import {Button} from "reactstrap";
import axios from 'axios';
//import download from 'js-file-download';
import { ToastContainer, toast } from "react-toastify";
class TableRowcheck extends Component {
    constructor(props) {
        super(props);
        //this.delete = this.delete.bind(this);
        this.download1 = this.download1.bind(this);
        this.delete = this.delete.bind(this);
    }
    download() {
        //axios.get('/api/doc/dowload/'+this.props.obj.idDoc);
        var iddowload = this.props.obj.idDoc;
        axios({
            url:'/api/doc/dowload/'+iddowload,
            moethod:'GET',
            responseType:'blob',
        }).then((response) => {
            const url =  window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download','file.docx');
            document.body.appendChild(link);
            link.click();
            toast.success('Download success');
        })
    }
    render() {
        return (
            <tr>  
                <td>
                    {this.props.obj.document}
                </td>
                
                <td>
                    {this.props.obj.message}                   
                </td>
                <td>
                    <Button>Download</Button>
                </td>
            </tr>
        );
    }
}

export default TableRowcheck;
