  hienThiFormChangePassword() {
    if (this.state.trangthaiedit === true) {
      return (
        <div>
          <Card>
            <CardHeader>
              <CardTitle>
                <Label tag="h4">Change Password</Label>
              </CardTitle>
            </CardHeader>
            <CardBody>
              <FormGroup row>
                <Label sm={5}>UserName</Label>
                <Col sm={7}>
                  <Input
                    value={this.state.dataedituser.iduser||''}
                    disabled
                    type="text"
                    name="iduser"
                    id=""
                    placeholder=""
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={5}>Current Password</Label>
                <Col sm={7}>
                  <Input
                    onChange={this.onChangeValue}
                    type="password"
                    name="currentPassword"
                    id=""
                    placeholder="Input current password"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={5}>New Password</Label>
                <Col sm={7}>
                  <Input
                    onChange={this.onChangeValue}
                    type="role"
                    name="newPassword"
                    id=""
                    placeholder="Input new password"
                  />
                </Col>
              </FormGroup>
              <Button onClick={this.onSubmitEdit} color="primary">
                Change User
              </Button>
              <Button onClick={()=>this.thayDoiTrangThaiEdit()} >Cancel</Button>
            </CardBody>
          </Card>
        </div>
      );
    }
  }
  thayDoiTrangThaiEdit() {
    this.setState({
      trangthaiedit: !this.state.trangthaiedit,
    });
  }

  onSubmitEdit(event) {
    event.preventDefault();
    if(this.state.currentPassword!==''&&this.state.newPassword!=='') {
      const b = {
        iduser: this.state.dataedituser.iduser,
        currentPassword: this.state.currentPassword,
        newPassword: this.state.newPassword,
      };
      console.log(b);
      axios.post("/api/user/changepass", b).
      then((res) => {
        if(res.data.success===true) {
          toast.success('change password success');
        }
        else
        {
          toast.error('current password is incorrect');
        }
      });
    }
    else
    {
      alert('Please fill out all information');
    }
    
  }
  hienThiDate=()=>{
    //document.getElementsByName('dob').value=this.state.dataedituser.dob;

    var d = new Date(this.state.dataedituser.dob),
    month = '' + (d.getMonth() + 1),
    day = '' + d.getDate(),
    year = d.getFullYear();

    if (month.length < 2) 
        month = '0' + month;
    if (day.length < 2) 
        day = '0' + day;

      return [year, month, day].join('-');
    
  }
  hienThiSex1=()=>{
    
    if(this.state.dataedituser.sex===true) {
      
      var a = document.getElementById('rMale');
      var b = document.getElementById('rFemale');
      a.checked=true;
      b.checked=false;
      return;
    }  else if(this.state.dataedituser.sex===false) {
      var a = document.getElementById('rFemale');
      var b = document.getElementById('rMale');
      a.checked=true;
      b.checked=false;
      return;
    }
  }
