                    type="text"
                    name="newphone"
                    id=""
                    defaultValue={this.state.dataedituser.phonenumber ||''}
                    placeholder="Input email"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>Address</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="newaddress"
                    id=""
                    defaultValue={this.state.dataedituser.address ||''}
                    placeholder="Input email"
                  />
                </Col>
              </FormGroup>
              {/* <FormGroup row>
                <Label sm={3}>Role</Label>
                <Col sm={9}>
                  <Input
                    value={this.state.dataedituser.role ||''}
                    onChange={this.onChangeValue}
                    type="select"
                    name="newrole"
                    id=""
                  >
                    <option>1</option>
                    <option>2</option>
                  </Input>
                </Col>
              </FormGroup> */}
              <Button onClick={this.onSubmitEditUser} color="primary">
                Add user
              </Button>
            </CardBody>
          </Card>
          </Col>

          <ToastContainer />
        </Row>
      </div>
    );
  }
