import React from "react";
import axios from "axios";
import {
  Card,
  CardHeader,
  CardBody,
  CardTitle,
  Table,
  Row,
  Col,
  Input,
  FormGroup,
  Button,
  Label,
} from "reactstrap";
// reactstrap components
//import { Card, CardHeader, CardBody, Row, Col } from "reactstrap";
import TableRowUser from "components/TableRow/TableRowUser.js";
import { ToastContainer, toast } from "react-toastify";
import { Redirect } from "react-router-dom";
class Icons extends React.Component {
  constructor(props) {
    super(props);
    this.onChangeValue = this.onChangeValue.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.onSelectValue = this.onSelectValue.bind(this);
    this.onSubmitEdit = this.onSubmitEdit.bind(this);
    this.state = {
      data1: [],
      dataedituser: [],
      firstsname: "",
      lastname: "",
      sex: "",
      dob: "",
      iduser: "",
      phonenumber: "",
      address: "",
      username: "",
      password: "",
      role: "1",
      currentPassword: "",
      newPassword: "",
      trangthai: false,
      trangthaiedit: false,
      newSex:""
    };
  }

  // thêm user
  onChangeValue(event) {
    var name = event.target.name;
    var value = event.target.value;
    this.setState({
      [name]: value,
    });
  }
  onSelectValue(event) {
    this.setState({
      role: event.target.select,
    });
  }
  onSubmit(event) {
    event.preventDefault();
    if(this.state.iduser!==''&&this.state.firstname!==''&&this.state.lastname!==''
    &&this.state.sex!==''&&this.state.dob!==''&&this.state.phonenumber!==''&&this.state.address!==''
    &&this.state.username!==''&&this.state.password!=='')
    {
      const formData = {
        username: this.state.username,
        password: this.state.password,
        role: this.state.role,
        iduser: this.state.iduser,
        firstname: this.state.firstname,
        lastname: this.state.lastname,
        sex1: this.state.sex,
        dob: this.state.dob,
        phonenumber: this.state.phonenumber,
        address: this.state.address,
      };
  
      console.log(formData);
      axios
        .post("/api/user/register", formData)
        .then((res) => {
          if (res.data.success === true) {
            toast.success("add user success");
            setTimeout(function () {
              window.location.reload(1);
            },1000);
          }
          else
          {
            toast.error("username already exists!");
          }
          
        })
        .catch((err) => {
          toast.error(`Upload Fail with status: ${err.statusText}`);
        });
    }
    else
    {
      alert('please fill out all information');
    }
    
  }
  // hiển thị list user
  componentDidMount() {
    axios
      .get("/api/user", {
        headers: {
          "Content-Type": "application/json",
          Authorization: JSON.parse(localStorage.getItem("authorization")),
        },
      })
      .then((response) => {
        console.log("ok");
        console.log(response.data.users);
        this.setState({ data1: response.data.users });
      })
      .catch(function (error) {
        console.log("loi123");
        console.log(error);
      });
  }
  tabUser = () =>
    this.state.data1.map((object, i) => (
      <TableRowUser
        onView={() => this.onClickEdit(object)}
        obj={object}
        key={i}
        onChangeTT={() => this.thayDoiTrangThaiEdit()}
      />
    ));

  // Check trạng thái thay đổi button
  checkNut() {
    if (this.state.trangthai === false) {
      return (
        <Button onClick={() => this.thayTrangThai()} size="sm" md="2">
          +
        </Button>
      );
    } else {
      return (
        <Button onClick={() => this.thayTrangThai()} size="sm" md="2">
          -
        </Button>
      );
    }
  }
  // click thay đổi trạng thái
  thayTrangThai = () => {
    this.setState({
      trangthai: !this.state.trangthai,
    });
  };
  // click trang thái thay đổi form
  hienThiForm() {
    if (this.state.trangthai === true) {
      return (
          <Card>
            <CardHeader>
              <CardTitle>
                <Label tag="h4">Create account</Label>
              </CardTitle>
            </CardHeader>
            <CardBody>
              <FormGroup row>
                <Label sm={3}>ID user</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="iduser"
                    id=""
                    placeholder="Input email"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>FirstName</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="firstname"
                    id=""
                    placeholder="Input email"
                  />
                </Col>
              </FormGroup>
              <FormGroup row>
                <Label sm={3}>LastName</Label>
                <Col sm={9}>
                  <Input
                    onChange={this.onChangeValue}
                    type="text"
                    name="lastname"
                    id=""
                    placeholder="Input email"
                  />
                </Col>
              </FormGroup>
              <FormGroup tag="fieldset" row>
                <Label sm={4}>Sex</Label>
                <Col sm={8}>
                  <FormGroup row check>
                    <Label sm={4} check>
                      <Input
                        type="radio"
                        name="sex"
                        value="Male"
                        checked={this.state.sex === "Male"}
                        onChange={this.onChangeValue}
                      />{" "}
                      Male
                    </Label>
                    <Label sm={4} check>
                      <Input
                        type="radio"
                        name="sex"
                        value="Female"
                        checked={this.state.sex === "Female"}
                        onChange={this.onChangeValue}
                      />{" "}
                      Female
                    </Label>
                  </FormGroup>
                </Col>
              </FormGroup>
              <FormGroup row>
